mapper.select:
SELECT G.GENRE_CODE
FROM MOVIEGENRE G
WHERE G.MOVI_ID = #{movi_id}

SELECT COUNT(MOVI_ID) FROM MOVIE M
M.MOVI_ID IN (SELECT S.MOVI_ID FROM SHOWSCHEDULE S WHERE S.SHOW_DATE >= TO_CHAR(SYSDATE, 'YYYYMMDD') AND S.SHOW_DATE < TO_CHAR(SYSDATE, 'YYYYMMDD') -7) AND M.OPEN_DATE <= TO_CHAR(SYSDATE, 'YYYYMMDD')
M.OPEN_DATE > TO_CHAR(SYSDATE, 'YYYYMMDD')
AND M.AVAI_AGE_CODE IN
#{avai_age}
AND M.MOVI_ID IN (SELECT MG.MOVI_ID FROM MOVIEGENRE MG WHERE MG.GENRE_CODE IN
#{genre}
)

SELECT B.RNUM, B.MOVI_ID, B.MOVI_NAME, B.AVAI_AGE, B.OPEN_DATE, B.RATING, B.ACCU_AUDIENCE, B.IMAGE FROM ( SELECT /*+index_desc(IDX_MOVIE_PK)*/ ROWNUM AS RNUM, A.MOVI_ID, A.MOVI_NAME, A.AVAI_AGE, A.OPEN_DATE, A.RATING, A.ACCU_AUDIENCE, A.IMAGE FROM( SELECT MOVI_ID, MOVI_NAME, (SELECT CODE_NAME FROM CODE C WHERE C.CODE_ID = M.AVAI_AGE_CODE) AS AVAI_AGE, OPEN_DATE, RATING, ACCU_AUDIENCE, IMAGE FROM MOVIE M
M.MOVI_ID IN (SELECT S.MOVI_ID FROM SHOWSCHEDULE S WHERE S.SHOW_DATE >= TO_CHAR(SYSDATE, 'YYYYMMDD') AND S.SHOW_DATE < TO_CHAR(SYSDATE, 'YYYYMMDD') -7) AND M.OPEN_DATE <= TO_CHAR(SYSDATE, 'YYYYMMDD')
M.OPEN_DATE > TO_CHAR(SYSDATE, 'YYYYMMDD')
AND M.AVAI_AGE_CODE IN
#{avai_age}
AND M.MOVI_ID IN (SELECT MG.MOVI_ID FROM MOVIEGENRE MG WHERE MG.GENRE_CODE IN
#{genre}
)
ORDER BY
RATING ACCU_AUDIDENCE MOVI_ID
) A WHERE ROWNUM <= #{page}*#{amount}) B WHERE B.RNUM > (#{page}-1)*#{amount}

SELECT B.RNUM, B.MOVI_ID, B.MOVI_NAME, B.AVAI_AGE, B.OPEN_DATE, B.RATING, B.ACCU_AUDIENCE, B.IMAGE FROM ( SELECT /*+index_desc(IDX_MOVIE_PK)*/ ROWNUM AS RNUM, A.MOVI_ID, A.MOVI_NAME, A.AVAI_AGE, A.OPEN_DATE, A.RATING, A.ACCU_AUDIENCE, A.IMAGE FROM( SELECT MOVI_ID, MOVI_NAME, (SELECT CODE_NAME FROM CODE C WHERE C.CODE_ID = M.AVAI_AGE_CODE) AS AVAI_AGE, OPEN_DATE, RATING, ACCU_AUDIENCE, IMAGE FROM MOVIE M WHERE
M.MOVI_ID IN (SELECT C.MOVI_ID FROM CAST C WHERE C.CAST_TYPE_CODE = #{cast_type_code} AND C.PEOP_NAME LIKE #{name}) M.MOVI_NAME = #{name}
) A WHERE ROWNUM <= #{page}*#{amount}) B WHERE B.RNUM > (#{page}-1)*#{amount}

mapper.update:
UPDATE MOVIE M SET M.MOVI_NAME = #{movi_name}, M.AVAI_AGE_CODE = #{avai_age_code}, M.RUN_TIME= #{run_time},
M.COMPANY = #{company},
M.MOVI_CONTENTS = #{movi_contents},
M.OPEN_DATE = #{open_date}
WHERE M.MOVI_ID = #{movi_id}

UPDATE MOVIE M SET M.IMAGE =

     #{image} 
     NULL
